import { movies } from "../movies";
import ADD_FAVLIST from '../actions/movieActions'

const initialState = {
    movies: movies,
    favList: [],
    sira: 0,
  };

const reducer = (state = initialState, action) => {
    switch (action.type) {

        case ADD_FAVLIST:
            return {
                ...state,
                favList: [...state.favList, action.payload]
            }

        default:
            return state;
    }


}

export default reducer;
