import { Switch, Route, NavLink } from "react-router-dom";
import Movie from "./components/Movie";
import FavMovie from "./components/FavMovie";
import { useDispatch, useSelector } from "react-redux";
import { addFavlist,onceki,sonraki,basadon } from "./store/actions/movieActions";

function App() {



  const favMovies = useSelector((state) => state.favList);
  const movies = useSelector((state) => state.movies);
  const sira = useSelector((state) => state.sira);
  const listede=useSelector(store=>store.favList.find(f=>f.id===movies[sira].id))

  console.log(movies)
  const dispatch = useDispatch();

  const handleAddFavlist = () => {
    if(listede){

    }
    dispatch(addFavlist(movies[sira]));
  }
  const sonrakiFilm = () => {
    dispatch(sonraki())
  }
  const oncekiFilm = () => {
    dispatch(onceki());
  }
  const bastakiFilm = () => {
    dispatch(basadon());
  };


  return (
    <div className="wrapper max-w-2xl mx-auto">
      <nav className="flex text-2xl pb-6 pt-8 gap-2 justify-center">
        <NavLink to="/" exact className="py-3 px-6 " activeClassName="bg-white shadow-sm text-blue-600">
          Filmler
        </NavLink>
        <NavLink to="/listem" className="py-3 px-6 " activeClassName="bg-white shadow-sm text-blue-600">
          Listem
        </NavLink>
      </nav>
      <Switch>
        <Route exact path="/">
          <Movie sira={sira} />

          <div className="flex gap-3 justify-end py-3">
          <button
              onClick={bastakiFilm}
              className="select-none px-4 py-2 border border-blue-700 text-blue-700 hover:border-blue-500 hover:text-blue-500"
            >
             Bastaki
            </button>
          <button
              onClick={oncekiFilm}
              className="select-none px-4 py-2 border border-blue-700 text-blue-700 hover:border-blue-500 hover:text-blue-500"
            >
             Onceki
            </button>
            <button
              onClick={sonrakiFilm}
              className="select-none px-4 py-2 border border-blue-700 text-blue-700 hover:border-blue-500 hover:text-blue-500"
            >
              SÄ±radaki
            </button>
            <button 
            onClick={handleAddFavlist}
            className="select-none px-4 py-2 bg-blue-700 hover:bg-blue-600 text-white">
              Listeme ekle
            </button>
          </div>
        </Route>

        <Route path="/listem">
          <div>
            {favMovies.map((movie) => (
              <FavMovie key={movie.id} title={movie.title} id={movie.id} />
            ))}
          </div>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
