export const ADD_FAVLIST = "ADD_FAVLIST";
export const REMOVE_FAVLIST="REMOVE_FAVLIST"

export const addFavlist = (movies) => {
    return ({
      type: ADD_FAVLIST,
      payload: movies,
    });
  };

  export const removeFavlist = (movieId) => ({
    type: 'REMOVE_FAVLIST',
    movieId,
  });
